
public class Arith_Test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int answer;
		answer = 3+21/5;
		System.out.println("3+21/5="+answer);
		answer = (3+21)/5;
		System.out.println("(3+21)/5="+answer);
		answer = 4+((15-7)/4);
		System.out.println("4+((15-7)/4)="+answer);
		answer = 13/3+5*2;
		System.out.println("13/3+5*2="+answer);
		answer = (int)(5.5%2.0);
		System.out.println("5.5%2.0="+answer);
		
		double answer1;
		answer1 = 3+21/5;
		System.out.println("3 + 21/5="+answer1);
		answer1 = (3+21) /5;
		System.out.println("(3+21) /5="+answer1);
		answer1 = 4 + ((15-7)/4); 
		System.out.println("4 + ((15-7)/4)"+answer1);
		answer1 = 13/3+5*2;
		System.out.println("13/3+5*2="+answer1);
		answer1 = 5.5%2.0;
		System.out.println("5.5%2.0="+answer1);
	}

}
